package com.example.internetaccess.core.di.network_module

import com.example.internetaccess.BuildConfig
import com.example.internetaccess.core.utils.LiveOkhttpLoggingInterceptor
import com.example.internetaccess.services.InternetServices
import com.google.gson.Gson
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.components.SingletonComponent
import okhttp3.Interceptor
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Converter
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory
import timber.log.Timber
import java.util.concurrent.TimeUnit
import javax.inject.Named
import javax.inject.Singleton

const val BASE_URL = "http://ec2-157-175-229-147.me-south-1.compute.amazonaws.com:8080/"
const val TOKEN =
    "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiJ9..qlZYFEzSfFOPApB_5W2agoUiThMHZ-fUFWYHdQUl-QIOkuy4-jbae3aJw6E5tOX1_B5lrnb49O6g86VXISiaQgQTYNeDZGJNgb1oYYFEpQ7DAD7Du9dtrK7lCWrZj0YcwCOUnR9knCNJsIdhjIEoa3jbVmd55bR8cCBc9tdg2r3FJKavDsE08Y0JqijzdrOp8BqJIih-0CIAAaV1_Q0lM7cMFzaC-ABbZFVhiklrTnDFZdLfDZVdknSHMnz8YHf8uH9IbBiR7SpN_zNMbW9RU2mJLOpf1RfdyB0VjvMbDYdhwAwDT_Y6PhQyDnapBmEX5iGEbCEWlCOQTLt5qCH5ug"

@Module
@InstallIn(SingletonComponent::class)
object NetworkModule {

    @Singleton
    @Provides
    fun provideInternetService(retrofit: Retrofit): InternetServices =
        retrofit.create(InternetServices::class.java)

    @Singleton
    @Provides
    fun provideGson() = Gson()

    @Provides
    fun provideConverterFactory(gson: Gson): Converter.Factory {
        return GsonConverterFactory.create(gson)
    }

    @Provides
    @Named("AuthInterceptor")
    fun provideAuthInterceptor(): Interceptor = Interceptor { chain ->
        val newBuilder = chain.request()
            .newBuilder()
        newBuilder.addHeader("Authorization", "Bearer $TOKEN")
        newBuilder.build().let { chain.proceed(it) }
    }


    @Provides
    @Named("LoggingInterceptor")
    @Singleton
    fun httpLoggingInterceptor(): Interceptor {
        return if (BuildConfig.DEBUG)
            HttpLoggingInterceptor { message ->
                Timber.tag("OkHttp").d(message)
            }.setLevel(HttpLoggingInterceptor.Level.BODY)
        else
            return LiveOkhttpLoggingInterceptor { message ->
                Timber.tag("OKHttp").d(message)

            }.setLevel(HttpLoggingInterceptor.Level.BODY)
    }


    @Singleton
    @Provides
    fun client(
        @Named("AuthInterceptor")
        authInterceptor: Interceptor,
        @Named("LoggingInterceptor")
        loggingInterceptor: Interceptor,
    ): OkHttpClient =
        OkHttpClient().newBuilder().run {
            addInterceptor(authInterceptor)
            addInterceptor(loggingInterceptor)
            connectTimeout(2, TimeUnit.MINUTES)
            readTimeout(2, TimeUnit.MINUTES)
            build()
        }

    @Singleton
    @Provides
    fun provideRetrofit(
        okHttpClient: OkHttpClient,
        converterFactory: Converter.Factory,
    ): Retrofit = Retrofit.Builder()
        .baseUrl(BASE_URL)
        .addConverterFactory(converterFactory)
        .client(okHttpClient)
        .build()
}


